//main
var React               = require('react')
var Icon = require('react-svg-icons')


module.exports = React.createClass({
  propTypes : {
    name: React.PropTypes.string,
    classes : React.PropTypes.string,
    label : React.PropTypes.string
  },
  
  render: function () {
    var name = 'hrc-icons_' + this.props.name
    var wrapperClass = 'hrc-icons hrc-icons_' + this.props.name + (typeof this.props.classes !== 'undefined' ? ' ' + this.props.classes : '')
    var label = typeof this.props.label !== 'undefined' ? this.props.label : this.props.name
    return (
      <span 
        role        = 'img'
        aria-label  = { label } 
        className   = { wrapperClass }>
          <Icon name  = { name }/>
      </span>
    )
  },
})
