//main
var React               = require('react')
var Grid 				= require('react-bootstrap/lib/Grid')


module.exports = React.createClass({
  propTypes : {
    alt: React.PropTypes.string 
  },
  
  render: function () {
    var alt = typeof this.props.alt !== undefined ? this.props.alt : "Loading content &hellip;"
    return (
      <div className="loading" >  
        <Grid className="loading-container">  
         <div role="presentation"><img alt={alt} src="./img/NPA-loading.gif"/></div>       
      </Grid>
      </div>
    )
  },
})
