//main
var React     = require('react')


// bootstrap
var Tooltip         = require('react-bootstrap/lib/Tooltip')
var OverlayTrigger  = require('react-bootstrap/lib/OverlayTrigger')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:TooltipWrap')



var TooltipWrap = React.createClass({

  propTypes : {    
    content: React.PropTypes.object,  
    tooltip: React.PropTypes.string,  
    placement: React.PropTypes.string
  },
  
  render: function () {    
//    log('props',this.props)
   
    var placement = typeof this.props.placement !== 'undefined' ? this.props.placement : 'top'
    var tooltip = (<Tooltip id='tooltip' >{this.props.tooltip}</Tooltip>)
            
    return (
      <OverlayTrigger placement={placement} overlay={tooltip}>
        <div className="tooltip-trigger">
          {this.props.content}
        </div>
      </OverlayTrigger>
    )
  }  
  
})


module.exports = TooltipWrap;