var React           = require('react')
var Fluxxor         = require('fluxxor')
var StoreWatchMixin = Fluxxor.StoreWatchMixin

//components
var EntityList            = require('components/list/EntityList')


//helpers

var _               = require('lodash')

var log       = require('debug')('src:components:pages:articles')

module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React),
    StoreWatchMixin('articles')
  ],

  getStateFromFlux: function () {
    var flux = this.getFlux()        
    return {
      loading: flux.store('articles').isLoading()
    }
  },

  render: function () {
//    log('props, state', this.props, this.state)
    var flux = this.getFlux()        
    if (this.state.loading) {
      return  (<Load />)
    } else {
      var entities = flux.store('articles').entities()
      var cols = [
                {type:'no', title: 'No',      attr:'id'},
                {type:'text', title: 'UDHR Article ',  attr:'title', markdown:true}                
        ]
      return (
          <EntityList
            collapsible = {false}
            className="articles-table"  
            linked ={false}
            cols = {cols}
            entities = {entities} 
            entityType = {flux.store('articles').getEntityType()} />                               
      )      
    }
    
  }
 

})
