var React       = require('react')
var Fluxxor     = require('fluxxor')

var CategoryListItem = require ('components/list/CategoryListItem')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:categoryList')

var CategoryList = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React)
  ],
  propTypes : {
    entities: React.PropTypes.array,
    entityType : React.PropTypes.string
  },
  
  render: function () {    
    log('props',this.props)
    
    return (
      <ul className={'hrc-category-list'}>
        {
          _.map(this.props.entities,function(item){
            return (
              <CategoryListItem
                key= {item.id}
                id = {item.id}
                entityType = {this.props.entityType}
                title = {item.title}
              />
            )
          },this)
        }
      </ul>
    )
      
  }, 
})

module.exports = CategoryList;
