var React       = require('react')
var Fluxxor     = require('fluxxor')
var StoreWatchMixin = Fluxxor.StoreWatchMixin




//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityList')



var EntityTable = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React)
  ],
  propTypes : {
    entities: React.PropTypes.array    
  },
  
  render: function () {    
    log('props',this.props)
    
    return (
      <ul className={'hrc-category-list'}>
        {
          _.map(this.props.entities,function(item){
            return (
              <li>
                <a href="#" onClick={this.handleCategoryClick(item.id,item.type)}>
                {item.title}
                </a>
              </li>
            )
          },this)
        }
      </ul>
    )
      
  },
  handleCategoryClick: function (id,type) {

    return function (e) {
      e.preventDefault()
      log('handleCategoryClick',type + '/' + id )
      this.getFlux().actions.entity.select(type,id)
    }.bind(this)
  },    
})

module.exports = EntityTable;
