//main
var React     = require('react')

//components
var TableBody            = require('components/list/TableBody')
var TableHead            = require('components/list/TableHead')

// bootstrap
var Table 				= require('react-bootstrap/lib/Table')


//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityList')



var EntityTable = React.createClass({

  propTypes : {
    cols: React.PropTypes.array,    
    entities: React.PropTypes.array    
  },
  
  render: function () {    
    log('props',this.props)
    
    return (
      <Table striped bordered condensed hover>
        <TableHead 
          cols = {_.pluck(this.props.cols,'title')} />
        <TableBody 
          cols = {_.pluck(this.props.cols,'field')}
          entities = {this.props.entities} />
      </Table>
    )
      
  }
})

module.exports = EntityTable;
