//main
var React     = require('react')

// components
var TableLinkRow = require('components/list/TableLinkRow')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListBody')



var TableBodySimple = React.createClass({

  propTypes : {
    rows: React.PropTypes.array,    
    cols: React.PropTypes.array,
    linked: React.PropTypes.bool
  },
  
  render: function () {    
    log('props',this.props)
    var rows =
            _.map(this.props.rows,function(row,i){
            return (
              <TableLinkRow
                linked={this.props.linked}
                cols={this.props.cols}
                row={row} />                
            )
           
          },this)
    return (
      <div className="hrc-tbody">
        {
          rows 
        }
      </div>
    )
  },
})


module.exports = TableBodySimple;