//main
var React     = require('react')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellBar')



var TableCellBar = React.createClass({

  propTypes : {
    row: React.PropTypes.object,    
    col: React.PropTypes.object    
  },
  
  render: function () {    
    log('props',this.props)
    
    var row = this.props.row
    var col = this.props.col
    
  
    
    
    return (
      <div className='cell-bar-wrap'>
        <span className="bar-label" 
          style={{color:col.value.color}}>
                  {row[col.value.attr]}
        </span>
        <span className="bar">
          {this.renderUnits(row[col.value.attr])}
        </span>
        <span className="bar-label bar-label-spacer"/> 
      </div>
    )
  },
  renderUnits : function(value) {
    var col = this.props.col
    
    var unitWidth = (100 / col.maxValue) + '%'
    
    return _.map(_.fill(Array(value), 1),function(unit,i){
      return (
              <span key={i} className="bar-unit" style={{backgroundColor:col.value.color,width:unitWidth}}/>
            )
    })
  }
})


module.exports = TableCellBar;