//main
var React     = require('react')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellBar')



var TableCellBar = React.createClass({

  propTypes : {
    entity: React.PropTypes.object,    
    col: React.PropTypes.object    
  },
  
  render: function () {    
    log('props',this.props)
    
    var entity = this.props.entity
    var col = this.props.col
    
    return (
      <div className='cell-bar-wrap'>
        <span className="bar-label" 
          style={{color:col.value.color}}>
                  {entity[col.value.attr]}
        </span>
        <span className="bar">
          <div className="bar-value" 
            style={{
                width:(entity[col.value.attr]/col.maxValue*100).toString() + '%',
                backgroundColor:col.value.color
              }}/>
        </span>
        <span className="bar-label bar-label-spacer"/> 
      </div>
    )
  }
})


module.exports = TableCellBar;