//main
var React     = require('react')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellIcon')



var TableCellIcon = React.createClass({

  propTypes : {
    entity: React.PropTypes.object,    
    col: React.PropTypes.object    
  },
  
  render: function () {    
    log('props',this.props)
    
    var entity = this.props.entity
    var col = this.props.col
    
    return (
      <div className='cell-icon-wrap'>
        <span className={(typeof col.class === 'function') ? col.class(entity.response) : col.class} />

      </div>
    )
  }
})


module.exports = TableCellIcon;