//main
var React     = require('react')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellBar')



var TableCellKey = React.createClass({

  propTypes : {    
    cellKey: React.PropTypes.object    
  },
  
  render: function () {    
    log('props',this.props)
        
    var cellKey = this.props.cellKey
    
    return (
      <div className='cell-key-wrap'>
       {this.renderKey(cellKey)}
      </div>
    )
  },
  renderKey : function(key) {
    if (key.type === 'units') {
      return (
        <div className="cell-key-units">
          { _.map(key.items,function(item){
              return (
                <div className="cell-key-item">
                  <span className="cell-key-item-block" style={{backgroundColor:item.color}}/>
                  <span className="cell-key-item-title" >{item.title}</span>
                </div>
              )
            })    
          }
        </div>
      )
    }
    else if (key.type === 'icons') {
      return (
        <div className="cell-key-units">
          { _.map(key.items,function(item){
              return (
                <div className="cell-key-item">
                  <span className={"cell-key-item-icon " + item.class} />
                  <span className="cell-key-item-title" >{item.title}</span>
                </div>
              )
            })    
          }
        </div>
      )
    }
  }
})


module.exports = TableCellKey;