//main
var React     = require('react')

var TableCellKey 				= require('components/list/TableCellKey')



//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListHead')



var TableHead = React.createClass({

  propTypes : {
    cols: React.PropTypes.array    
  },
  
  render: function () {    
    log('props',this.props)
    return (
      <thead>
        <tr>
          {
            _.map(this.props.cols,function(col,i){
              return (
                <th key={i} className={'hrc-cell hrc-header-cell header-cell-'+col.type}>
                  {this.renderCell(col)}
                </th>
              )
            },this)

          }
        </tr>
      </thead>
    )
  },
  renderCell:function(col) {
    if (col.type === 'key-links') {
      return <TableCellKey cellKey={col.key}/>
    } else {
      if ( typeof col.title !== 'undefined' ) {
        return col.title
      } else {
        return null
      }
    }
  }
})


module.exports = TableHead;