var React         = require('react')
var Fluxxor         = require('fluxxor')
//components
var Icon        = require('components/Icon')

var log       = require('debug')('src:components:nav-carousel')

module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React),
  ],
  render: function () {
    log('props', this.props)
    
    

    return (
      <nav className="hrc-nav-carousel">
        <ul className="hrc-nav-carousel-list list-inline" role="presentation">
          <li className="hrc-nav-carousel-li-back" role="presentation">
            <a href="#" className={'btn'}  onClick={this.handleBackClick()}  title="Close" aria-label="Close page to go back to previous view">
              <div className="small hrc-nav-carousel-label" >
                Close
              </div>
              <div className="hrc-carousel-icon hrc-carousel-icon-close">
                <Icon name="close" />
              </div>
            </a>
          </li>
        </ul>
      </nav>


    )
  },

  handleBackClick: function () {
    var self = this
    return function (e) {
      e.preventDefault()
      self.getFlux().actions.navigateBack()
    }
  },


})
