// react
var React         = require('react')
var Fluxxor      = require('fluxxor')

//components
var SelectTaxonomy = require('components/select-taxonomy')

//bootstrap
var Grid 				= require('react-bootstrap/lib/Grid')
var Row 				= require('react-bootstrap/lib/Row')
var Col 				= require('react-bootstrap/lib/Col')

//helpers
var _         = require('lodash')
var log       = require('debug')('src:components:nav-categories')


module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React)
  ],
  isActive : function(actives){
    return actives.indexOf(this.props.active) > -1        
  },
  render: function () {
    log('props', this.props)
    var flux = this.getFlux();
    var navItems = [
      {slug:'issues',route:'issue',title:'Issue',active:this.isActive(['issues','issue'])},
      {slug:'groups',route:'group',title:'Population Group',active:this.isActive(['groups','group'])},
      {slug:'agencies',route:'agency',title:'Government Agency',active:this.isActive(['agencies','agency'])},
      {slug:'treatybodies',route:'treatybody',title:'UN Treaty Body',active:this.isActive(['treatybodies','treatybody'])}
    ]
    var type = 'exploreby'
   
    return (
      <div className='nav-primary'>
        <Grid className=''>
          <Row className='nav-primary-row'>
            <Col md={2} className='nav-primary-label serif hidden-xs hidden-sm'>
              <div>Explore</div>
              <div>actions and recommendations by</div>
            </Col>
            <Col md={10} xs={12}>
              <SelectTaxonomy 
                flux = {flux}
                items = {navItems}
                type = {type}
              />
            </Col>
          </Row>
        </Grid>
      </div>
    )
  }
})
