var React           = require('react')
var Fluxxor         = require('fluxxor')
var StoreWatchMixin = Fluxxor.StoreWatchMixin

//components


//helpers

var _               = require('lodash')

var log       = require('debug')('src:components:pages:articles')

module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React),
    StoreWatchMixin('issues','articles')
  ],

  getStateFromFlux: function () {
    var flux = this.props.flux
    return {
      issuesLoading: flux.store('issues').isLoading(),
      articlesLoading : flux.store("articles").isLoading(),     
    }
  },

  render: function () {
//    log('props, state', this.props, this.state)
    var flux = this.props.flux

    return (
      <div className='container-fluid main-content'>
      
        { this.renderMain() }

      </div>
    )
  },
  renderMain : function (){
    var flux = this.props.flux

    return (
      <div>Articles</div>
    )
  },
 

})
