var React         = require('react')
var Fluxxor       = require('fluxxor')
//mixins
var FluxMixin           = Fluxxor.FluxMixin(React)


//helpers
var _         = require('lodash')

var Grid 				= require('react-bootstrap/lib/Grid')
var Row 				= require('react-bootstrap/lib/Row')
var Col 				= require('react-bootstrap/lib/Col')

var log       = require('debug')('src:components:intro')


module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React)
  ],

  render: function () {

    log('props, state', this.props, this.state)
    var flux = this.getFlux()    
    var SelectView
    var navItems = [
      {slug:'issues',title:'Issues'},
      {slug:'groups',title:'Population Groups'},
      {slug:'agencies',title:'Government Agencies'},
      {slug:'treatybodies',title:'Treaty Bodies'}
    ]

      

      SelectView = ( [
      <Grid className="intro-explore-hint">                      
        <h4 className="serif intro-explore">Explore Actions and Recommendations by</h4>
      </Grid>,
      <div className="intro-issue-select nav-primary">  
        <Grid>              
          <ul className="list-inline nav-primary-list">
            {
              _.map(navItems,function (item, i, items) {
                var width = 1/items.length * 100 + '%'
                var className = 'nav-primary-li intro-li'
                return (
                  <li className={className}  style={{width:width}}>
                    <a
                      className='btn'
                      href='#'
                      onClick={this.handleCategoryClick(item.slug)}>
                      <span className={'icon-'+ item.slug}></span>
                      <div className='title'>{item.title}</div>
                    </a>
                  </li>
                )
              },this)
            }
          </ul>
        </Grid>
      </div>,
      <Grid>
        <div className="intro-read-more">
          <p>or learn more about this tool <a href="#" onClick={this.handleNavClick('about')}>here</a></p>
        </div>
      </Grid>]  
      )
    
    return (
      <div className='intro text-center'>
        <Grid>
          <Row className="intro-content">
            <Col md={10} xs={10} mdPush={1} xsPush={1}>
              <h1 className='serif'>
                New Zealand's national Plan of Action for the 
                Protection and Promotion of Human Rights 2015 &ndash; 2019
              </h1>
              <p>
                This interactive tool sets out the actions the Government is taking as a result of the commitments 
                it made through its second Universal Periodic Review (UPR) before the United Nations Human Rights Council in 2014
              </p>
            </Col>
          </Row>
        </Grid>
       {SelectView}
      </div>
    )
  },

  handleCategoryClick: function (slug) {   
    return _.bind(function (e) {
      e.preventDefault()
      this.getFlux().actions.exploreby(slug)
    },this)
  },
  handleNavClick: function (page) {
    var flux = this.props.flux

    return function (e) {
      e.preventDefault()
      flux.actions.navPage(page)
    }
  },          
})
