var React           = require('react')
var Fluxxor         = require('fluxxor')
var StoreWatchMixin = Fluxxor.StoreWatchMixin

//components
var EntityTable            = require('components/list/EntityTable')


//helpers

var _               = require('lodash')

var log       = require('debug')('src:components:pages:glossary')

module.exports = React.createClass({
  mixins: [
    Fluxxor.FluxMixin(React),
    StoreWatchMixin('terms')
  ],

  getStateFromFlux: function () {
    var flux = this.getFlux()        
    return {
      termsLoading: flux.store('terms').isLoading(),
      
    }
  },

  render: function () {
//    log('props, state', this.props, this.state)
    var flux = this.getFlux()        
    if (this.state.termsLoading ) {
      return  (
        <Load />
      )
    } else {
      var entities = flux.store('terms').entities()
      var cols = [
                {type:'label', title: 'Term',            attr:'title'},
                {type:'text', title: 'Definition',      attr:'definition'}
        ]
      return (
          <EntityTable
            collapsible = {false}  
            className="terms-table"  
            linked= {false}
            cols = {cols}
            entities = {entities} 
            entityType = {flux.store('terms').getEntityType()} />           
                    
      )      
    }
    
  }
 

})
