//main
var React     = require('react')
var log         = require('debug')('src:components:show-more')
module.exports = React.createClass({
 
  render: function () {
    log('props',this.props)
    if (this.props.totalItems > this.props.maxItems && this.props.collapsible) {
     
      return (
        <div className="show-more">
          <a href="#" onClick={this.props.toggleCollapse}>
            <div className="show-more-note">
              {this.props.collapsed 
                  ? "Showing " + this.props.maxItems 
                          + " of " + this.props.totalItems + " " + this.props.showMoreLabel
                  : "Showing all " + this.props.totalItems + " " + this.props.showMoreLabel
                }
            </div>  
            <div className="show-more-hint strong">
              {this.props.collapsed ? 'Show all' : 'Show less'}
            </div>
          </a>
        </div>
      )
    } else {
      return null;
    }
  },
})
