//main
var React     = require('react')

//components
var Icon        = require('components/icon')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellIcon')



var TableCellIcon = React.createClass({

  propTypes : {
    row: React.PropTypes.object,    
    col: React.PropTypes.object
  },
  
  render: function () {    
//    log('props',this.props)
    
    var row = this.props.row
    var col = this.props.col
    
    var iconClass = (typeof col.class === 'function') 
            ? col.class(row[col.attr],col.attr) 
            : col.class
    var iconLabel = (typeof col.label === 'function') 
            ? col.label(row[col.attr],col.attr) 
            : col.label
    return (
      <div role="presentation" className='cell-icon-wrap'>
        <Icon
          label ={ iconLabel } 
          name  ={ iconClass } 
        />
      </div>
    )
  }
})


module.exports = TableCellIcon;