//main
var React     = require('react')

//helpers
var _           = require('lodash')
var marked      = require('utils/parse-markdown')
var log         = require('debug')('src:components:entityListCellText')



var TableCellText = React.createClass({

  propTypes : {
    text: React.PropTypes.string,
    className: React.PropTypes.string
  },
  
  render: function () {    
//    log('props',this.props)
    var className = 'cell-text-wrap ' + (this.props.className ? this.props.className : '')
    return (
      <div className={className}>
            {this.props.nestBefore}            
            <div className="marked-inner-html"
              dangerouslySetInnerHTML={{
                __html: marked(this.props.text, {sanitize: true})
              }} >
            </div>
            {this.props.nestAfter}
      </div>
    )
  }  
})


module.exports = TableCellText;