//main
var React     = require('react')

//helpers
var _           = require('lodash')
var log         = require('debug')('src:components:entityListCellBar')



var TableHeaderCellKey = React.createClass({

  propTypes : {    
    cellLink: React.PropTypes.object    
  },
  
  render: function () {    
    log('props',this.props)
        
    return (
      <div className='cell-link-wrap'>
       {this.renderLink()}
      </div>
    )
  },
  renderLink : function() {
    var link = this.props.cellLink
    
    if (typeof link !== 'undefined') {
      return (
        <a title={link.title} 
          onClick={link.onClick}
          className="cell-link" href="#">        
            <span role="img" aria-label={link.title} className={link.icon} />
            <span>{(!link.hiddenTitle) ? link.title : ''}</span>
        </a>
      )
    } else {
      return null
    }
    
  }
})


module.exports = TableHeaderCellKey;