var React         = require("react"),
    Router        = require("react-router"),
    Route         = Router.Route,
    DefaultRoute  = Router.DefaultRoute;


var Main           = require('components/handlers/main')
var Intro          = require('components/handlers/intro')
var Page           = require('components/handlers/page')
var Explore        = require('components/handlers/explore')
var Issue          = require('components/handlers/issue')
var Group          = require('components/handlers/group')
var Agency         = require('components/handlers/agency')
var Treatybody     = require('components/handlers/treatybody')
var Action         = require('components/handlers/action')
var Recommendation = require('components/handlers/recommendation')


require('string.prototype.startswith')

var routes = (
  <Route path={"/"} handler={Main} name="home" >
    <DefaultRoute name="intro" handler={Intro} />
    <Route path={"/explore-by/:category"} handler={Explore} name="explore-by" ignoreScrollBehavior/>
    <Route path={"/issue/:id"} handler={Issue} name="issue" />
    <Route path={"/group/:id"} handler={Group} name="group" />
    <Route path={"/agency/:id"} handler={Agency} name="agency" />
    <Route path={"/treatybody/:id"} handler={Treatybody} name="treatybody" />
    <Route path={"/action/:id"} handler={Action} name="action" />
    <Route path={"/recommendation/:id"} handler={Recommendation} name="recommendation" />
    <Route path={"/page/:id"} handler={Page} name="page" />
  </Route>
);

module.exports = routes;
