var React         = require("react"),
    Router        = require("react-router"),
    Route         = Router.Route,
    DefaultRoute  = Router.DefaultRoute;


var Main          = require('components/main')
var Intro         = require('components/pages/intro')
var About         = require('components/pages/about')
var Glossary         = require('components/pages/glossary')
var Articles         = require('components/pages/articles')

var Explore         = require('components/explore')
var Issue         = require('components/explore/issue')
var Group         = require('components/explore/group')
var Agency         = require('components/explore/agency')
var Treatybody         = require('components/explore/treatybody')
var Action         = require('components/subjects/action')
var Recommendation         = require('components/subjects/recommendation')


require('string.prototype.startswith')

var routes = (
  <Route path={"/"} handler={Main} name="home" >
    <DefaultRoute name="intro" handler={Intro} />
    <Route path={"/explore-by/:category"} handler={Explore} name="explore-by" />
    <Route path={"/issue/:id"} handler={Issue} name="issue" />
    <Route path={"/group/:id"} handler={Group} name="group" />
    <Route path={"/agency/:id"} handler={Agency} name="agency" />
    <Route path={"/treatybody/:id"} handler={Treatybody} name="treatybody" />
    <Route path={"/action/:id"} handler={Action} name="action" />
    <Route path={"/recommendation/:id"} handler={Recommendation} name="recommendation" />
    <Route path={"/about"} handler={About} name="about" />
    <Route path={"/glossary"} handler={Glossary} name="glossary" />
    <Route path={"/articles"} handler={Articles} name="articles" />
  </Route>
);

module.exports = routes;
