// flux
var Fluxxor = require('fluxxor')
var actions = require('actions')
var createPdf = require('pdfmake-browserified')

// helpers
var _       = require('lodash')
var log     = require('debug')('src:node_modules:stores:print-store')

// util
var referencing = require('utils/referencing')

// Route Store binds route changing actions and triggers router transitions
var PrintStore = Fluxxor.createStore({

  initialize: function (options) {
    this.styles = {
      header1: {
        fontSize: 22,
        bold: true
      },
      header2: {
        fontSize: 15,
        bold: true,
        color: '#7696A5'
      },
      header3: {
        fontSize: 12,
        bold: true
      },
      blue: {
        color: '#008399'
      }
    }
    // Defining the category structures
    this.structure = {
      agency: {
        mainTitle: 'ABOUT',
        main: 'description',
        sideTitle: 'WEBSITE',
        side: 'link'
      },
      group: {
        mainTitle: 'COMMENTARY',
        main: 'commentary',
        sideTitle: 'ABOUT THIS GROUP',
        side: 'description'
      },
      issue: {
        mainTitle: 'CURRENT CONTEXT',
        main: 'currentcontext',
        sideTitle: 'OTHER ISSUES RAISED BY CIVIL SOCIETY',
        side: 'otherissuesraised'
      },
      treatyBody: {
        mainTitle: 'ABOUT',
        main: 'description',
        sideTitle: 'CONCLUDING OBSERVATIONS',
        side: 'concludingobservations'
      }
    }
    // route actions
    this.bindActions(
      actions.constants.PRINT, this.print
    )
  },

  print: function (payload) {
    this.waitFor(['actions', 'issues', 'groups', 'agencies', 'pages'],
    function (actionStore, issueStore, groupStore, agencyStore, pageStore) {
      var content, category, relatedEntities
      switch (payload.type) {
        case 'action' :
          var action = actionStore.findOne({id: payload.id})
          content = this.getAction(action)
          break
        case 'issue' :
          category = issueStore.findOne({id: payload.id})
          relatedEntities = referencing(this.flux, {
            entity: category,
            attr: 'issues',
            from: 'actions'
          })
          content = this.getCategory(payload, category, relatedEntities)
          break
        case 'group' :
          category = groupStore.findOne({id: payload.id})
          relatedEntities = referencing(this.flux, {
            entity: category,
            attr: 'groups',
            from: 'actions'
          })
          content = this.getCategory(payload, category, relatedEntities)
          break
        case 'agency' :
          category = agencyStore.findOne({id: payload.id})
          relatedEntities = referencing(this.flux, {
            entity: category,
            attr: 'agencies',
            from: 'actions'
          })
          content = this.getCategory(payload, category, relatedEntities)
          break
        case 'all' :
          relatedEntities = actionStore.entities()
          var relatedAgencyEntities = agencyStore.entities()
          var relatedGroupEntities = groupStore.entities()
          var relatedIssueEntities = issueStore.entities()
          content = this.getAll(payload, relatedEntities, relatedAgencyEntities, relatedGroupEntities, relatedIssueEntities)
          break
      }
      createPdf(content).download()
    })
  },

  getAction: function (action) {
    var actionContent = {
      content: [
        {text: action.title, style: 'header1', margin: [0, 20]},
        {text: 'DESCRIPTION', style: 'header2', margin: [0, 10]},
        {
          columns: [
            {
              stack: [
                {text: action.description},
                {text: 'DESIRED OUTCOME', style: 'header2', margin: [0, 10]},
                {text: action.desiredoutcome},
                {text: 'MEASURES', style: 'header2', margin: [0, 10]},
                {text: action.measures},
                {text: 'TARGET DATE', style: 'header2', margin: [0, 10]},
                {text: action.targetdate}
              ],
              width: '80%'
            },
            [
              {text: 'Issue', style: 'header3', margin: [0, 10]},
              {text: action.issues, style: 'blue'},
              {text: 'Population Groups', style: 'header3', margin: [0, 10]},
              {text: action.groups, style: 'blue'},
              {text: 'Government Agency', style: 'header3', margin: [0, 10]},
              {text: action.agencies, style: 'blue'}
            ]
          ]
        }
      ],
      styles: this.styles,
      defaultStyle: {
        columnGap: 20
      }
    }
    return actionContent
  },

  getCategory: function (payload, category, relatedEntities) {
    var relatedEntitiesAction = _.map(relatedEntities, function (value, key) {
      return {stack: this.getAction(value).content}
    }, this)

    var categoryContent = {
      content: [
        {text: category.title, style: 'header1', margin: [0, 20], pageBreak: 'after'},
        this.getCategoryContent(category, payload.type, relatedEntities),
        {stack: relatedEntitiesAction}
      ],
      styles: this.styles,
      defaultStyle: {
        columnGap: 20
      }
    }
    return categoryContent
  },

  getCategoryContent: function (category, type, relatedEntities) {
    var relatedEntitiesIndex = _.map(relatedEntities, function (value, key) {
      return {columns: [{width: '10%', text: value.id}, {width: '90%', text: value.title, style: 'index'}]}
    })

    var categoryContent = [
      {text: category.title, style: 'header1', margin: [0, 20]},
      {
        columns: [
          {
            stack: [
              {text: this.structure[type].mainTitle, style: 'header2', margin: [0, 10]},
              {text: category[this.structure[type].main]}
            ],
            width: '80%'
          },
          [
            {text: this.structure[type].sideTitle, style: 'header3', margin: [0, 10]},
            {text: category[this.structure[type].side], style: 'blue'}
          ]
        ],
        pageBreak: 'after'
      },
      {text: 'ACTION INDEX', style: 'header1', margin: [0, 20]},
      {text: relatedEntities.length + ' RELATED ACTIONS', style: 'header2', margin: [0, 20]},
      {stack: relatedEntitiesIndex, pageBreak: 'after'}
    ]
    return categoryContent
  },

  getAll: function (payload, relatedEntities, agencyEntities, groupEntities, issueEntities) {
    var relatedGroupEntities = _.map(groupEntities, function (value, key) {
      return this.getCategoryContent(value, 'group', groupEntities)
    }, this)

    var relatedIssueEntities = _.map(issueEntities, function (value, key) {
      return this.getCategoryContent(value, 'issue', issueEntities)
    }, this)

    var relatedAgencyEntities = _.map(agencyEntities, function (value, key) {
      return this.getCategoryContent(value, 'agency', agencyEntities)
    }, this)

    var relatedEntitiesAction = _.map(relatedEntities, function (value, key) {
      return {stack: this.getAction(value).content, pageBreak: 'before'}
    }, this)

    var allContent = {
      content: [
        {text: 'FULL REPORT', style: 'header1', margin: [0, 20], pageBreak: 'after'},
        {text: 'CATEGORY: GROUPS', style: 'header1', margin: [0, 20]},
        {
          stack: relatedGroupEntities,
          pageBreak: 'after'
        },
        {text: 'CATEGORY: ISSUES', style: 'header1', margin: [0, 20]},
        {
          stack: relatedIssueEntities,
          pageBreak: 'after'
        },
        {text: 'CATEGORY: AGENCIES', style: 'header1', margin: [0, 20]},
        {
          stack: relatedAgencyEntities,
          pageBreak: 'after'
        },
        {stack: relatedEntitiesAction}
      ],
      styles: this.styles,
      defaultStyle: {
        columnGap: 20
      }
    }
    return allContent
  }
})

module.exports = PrintStore
