// react
var React           = require('react')

var checkSMART = require('utils/checkSMART')

module.exports = function(target) {
  
  if (target === 'class') {    
    
    var getSMARTiconClass = function(criterion, attr) {
      
      var icon_class = 'smart-' +  
            (attr === 'isspecific' 
              ? 's'   
              : (attr === 'ismeasurable' 
                ? 'm'   
                : (attr === 'isassignable' 
                  ? 'a'   
                  : (attr === 'isresultoriented' 
                    ? 'r'   
                    : (attr === 'istimebound' 
                      ? 't' : '')))))    
      return icon_class + (checkSMART(criterion) ? '-pass-active' : '-fail-active')
    }
    
    return getSMARTiconClass;
    
  } else if (target === 'label') {
    
    var getSMARTiconLabel = function(criterion, attr) {
      var icon_label =  
            (attr === 'isspecific' 
              ? 'Specific'   
              : (attr === 'ismeasurable' 
                ? 'Measurable'   
                : (attr === 'isassignable' 
                  ? 'Assignable'   
                  : (attr === 'isresultoriented' 
                    ? 'Result-oriented'   
                    : (attr === 'istimebound' 
                      ? 'Timebound' : ''))))) 
                      
      return icon_label + (checkSMART(criterion) ? ': met' : ': not met')
    }
    return getSMARTiconLabel;
  } else if (target === 'labelcomponent') {
    
    var getSMARTiconLabelComponent = function(criterion, attr) {
      var icon_label =  
            (attr === 'isspecific' 
              ? 'Specific'   
              : (attr === 'ismeasurable' 
                ? 'Measurable'   
                : (attr === 'isassignable' 
                  ? 'Assignable'   
                  : (attr === 'isresultoriented' 
                    ? 'Result-oriented'   
                    : (attr === 'istimebound' 
                      ? 'Timebound' : ''))))) 
                      
      return (
        <div className="smart-label hidden-xs"> 
          <div className="smart-label-type">
            {icon_label}
          </div>
          <div className="smart-label-result">
            {(checkSMART(criterion) ? 'Met' : 'Not met')}
          </div>
        </div>
      )
    }
    return getSMARTiconLabelComponent;
  }
}

