module.exports = function(target) {
  var checkCriterion = function (criterion) {
    return criterion === 1 || criterion === '1' || criterion === 'y' || criterion === 'yes'
  }
  
  if (target === 'class') {    
    
    var getSMARTiconClass = function(criterion, attr) {
      
      var icon_class = 'icon-smart-sm-' + 
            (attr === 'isspecific' 
              ? 's'   
              : (attr === 'ismeasurable' 
                ? 'm'   
                : (attr === 'isassignable' 
                  ? 'a'   
                  : (attr === 'isresultoriented' 
                    ? 'r'   
                    : (attr === 'istimebound' 
                      ? 't' : '')))))    
      return icon_class + (checkCriterion(criterion) ? ' active' : '')
    }
    
    return getSMARTiconClass;
    
  } else if (target === 'label') {
    
    var getSMARTiconLabel = function(criterion, attr) {
      var icon_label =  
            (attr === 'isspecific' 
              ? 'Specific'   
              : (attr === 'ismeasurable' 
                ? 'Measurable'   
                : (attr === 'isassignable' 
                  ? 'Assignable'   
                  : (attr === 'isresultoriented' 
                    ? 'Result oriented'   
                    : (attr === 'istimebound' 
                      ? 'Timebound' : ''))))) 
                      
      return icon_label + (checkCriterion(criterion) ? ': satisfied' : ': not satisfied')
    }
    
    return getSMARTiconLabel;
  }
}

