var checkSMART = require('utils/checkSMART')

module.exports = function(target) {
  
  if (target === 'class') {    
    
    var getSMARTiconClass = function(criterion, attr) {
      
      var icon_class = 'smart-' + 
            (attr === 'isspecific' 
              ? 's'   
              : (attr === 'ismeasurable' 
                ? 'm'   
                : (attr === 'isassignable' 
                  ? 'a'   
                  : (attr === 'isresultoriented' 
                    ? 'r'   
                    : (attr === 'istimebound' 
                      ? 't' : '')))))    
      return icon_class + (checkSMART(criterion) ? '' : '-fail')
    }
    
    return getSMARTiconClass;
    
  } else if (target === 'label') {
    
    var getSMARTiconLabel = function(criterion, attr) {
      var icon_label =  
            (attr === 'isspecific' 
              ? 'Specific'   
              : (attr === 'ismeasurable' 
                ? 'Measurable'   
                : (attr === 'isassignable' 
                  ? 'Assignable'   
                  : (attr === 'isresultoriented' 
                    ? 'Result oriented'   
                    : (attr === 'istimebound' 
                      ? 'Timebound' : ''))))) 
                      
      return icon_label + (checkSMART(criterion) ? ': met' : ': not met')
    }
    
    return getSMARTiconLabel;
  }
}

