var moment = require('moment')
module.exports = function (str) {
  // try fully specified date
  if (moment(str, 'DD/MM/YYYY').format() !== 'Invalid date') {
    return moment(str, 'DD/MM/YYYY')
  // try fully specified date
  } else if (moment(str, 'MM/DD/YYYY').format() !== 'Invalid date') {
    return moment(str, 'MM/DD/YYYY')
  // try month and year
  } else if (moment(str, 'MM/YYYY').format() !== 'Invalid date') {
    return moment(str, 'MM/YYYY')
  // try year only
  } else if (moment(str, 'YYYY').format() !== 'Invalid date') {
    return moment(str, 'YYYY')
  } else {
    return str
  }
}

