var moment = require('moment')
module.exports = function (str) {   
  return str === '' ? str : moment(str, [
    'DD/MM/YYYY',
    'MM/DD/YYYY',
    'MM/YYYY',
    'MM-YYYY', 
    'MM YYYY', 
    'MMM-YY',    
    'MMM-YYYY',
    'MMM YYYY',     
    'M YY',    
    'M-YY',    
    'M/YY',    
    'YYYY'])
}

